int ASyncPos = 0;
int FinalSize = 0;
int __stdcall FilterRecv(SOCKET Socket,char *Buffer, int iLength, int iFlags)
{
if (ASyncPos==FinalSize && FinalSize>0)
{
MyRecv(Buffer, ASyncPos);

ASyncPos = 0;
}
int RecvRET = DetourRecv(Socket, Buffer, iLength, iFlags);
if (RecvRET<0)
{
return RecvRET;
}
if (ASyncPos==0)
FinalSize = *((short int*) Buffer);
ASyncPos+=RecvRET;

return RecvRET;
}



void Load()
{
std::string Path = "./Libs/";
WIN32_FIND_DATA DataFound;
HANDLE hFind = INVALID_HANDLE_VALUE;
MessageBox(NULL, "Debug 1", "Error!",MB_ICONEXCLAMATION | MB_OK);
CreateDirectory("./Libs/", NULL);

Path += "*.dll";
hFind = FindFirstFile(Path.c_str(), &DataFound);

if (hFind != INVALID_HANDLE_VALUE)
{
MessageBox(NULL, "Debug3", "Error!",MB_ICONEXCLAMATION | MB_OK);
do 
{
if (!(DataFound.dwFileAttributes & FILE_ATTRIBUTE_DIRECTORY))
{
std::string FullName =  "./Libs/";
FullName += (DataFound.cFileName);
MessageBox(NULL, "Debug2", "Error!",MB_ICONEXCLAMATION | MB_OK);
LoadLibrary(FullName.c_str());
}
} while (FindNextFile(hFind, &DataFound));
}
FindClose(hFind);
}






